def verify_and_fix_database():
    """V√©rifie et r√©pare la base de donn√©es"""
    with app.app_context():
        try:
            print("üîç V√©rification de la base de donn√©es...")
            
            # Liste des tables critiques
            critical_tables = [
                'element_logigramme',
                'lien_logigramme',
                'processus_activite'
            ]
            
            for table in critical_tables:
                try:
                    # V√©rifier si la table existe
                    result = db.session.execute(
                        text(f"SELECT name FROM sqlite_master WHERE type='table' AND name='{table}'")
                    ).fetchone()
                    
                    if not result:
                        print(f"‚ùå Table {table} n'existe pas!")
                        # Cr√©er la table si elle n'existe pas
                        db.create_all()
                        print(f"‚úÖ Table {table} cr√©√©e")
                    
                    # ‚úÖ CORRECTION : Ajouter text() ici aussi
                    # V√©rifier si client_id existe
                    result = db.session.execute(
                        text(f"PRAGMA table_info({table})")
                    ).fetchall()
                    columns = [col[1] for col in result]
                    
                    if 'client_id' not in columns:
                        print(f"‚ö†Ô∏è Colonne client_id manquante dans {table}")
                        try:
                            db.session.execute(
                                text(f"ALTER TABLE {table} ADD COLUMN client_id INTEGER")
                            )
                            db.session.commit()
                            print(f"‚úÖ Colonne client_id ajout√©e √† {table}")
                        except Exception as e:
                            print(f"‚ùå Impossible d'ajouter client_id √† {table}: {e}")
                            
                except Exception as e:
                    print(f"‚ùå Erreur v√©rification table {table}: {e}")
            
            print("‚úÖ V√©rification termin√©e")
            
        except Exception as e:
            print(f"‚ùå Erreur v√©rification base: {e}")

# Appeler cette fonction au d√©marrage
with app.app_context():
    verify_and_fix_database()
    
