@app.route('/admin/fix-zone-risque-table')
@login_required
def fix_zone_risque_table():
    """Script de correction pour la table zone_risque_processus"""
    
    # Autoriser super admin et admin client
    if not (current_user.role == 'super_admin' or 
            (current_user.role == 'admin' and current_user.is_client_admin)):
        flash('Acc√®s non autoris√©', 'error')
        return redirect(url_for('dashboard'))
    
    try:
        from sqlalchemy import text
        
        print("üîß D√©but correction table zone_risque_processus...")
        
        # 1. V√©rifier si la table existe
        tables = db.session.execute(text(
            "SELECT name FROM sqlite_master WHERE type='table' AND name='zone_risque_processus'"
        )).fetchone()
        
        if not tables:
            return "‚ùå Table zone_risque_processus n'existe pas", 404
        
        # 2. V√©rifier les colonnes existantes
        columns = db.session.execute(text("PRAGMA table_info(zone_risque_processus)")).fetchall()
        column_names = [col[1] for col in columns]
        print(f"üìã Colonnes actuelles: {column_names}")
        
        # 3. Ajouter la colonne client_id si manquante
        if 'client_id' not in column_names:
            print("‚ûï Ajout colonne client_id...")
            db.session.execute(text("ALTER TABLE zone_risque_processus ADD COLUMN client_id INTEGER"))
            db.session.commit()
            print("‚úÖ Colonne client_id ajout√©e")
        else:
            print("‚úÖ Colonne client_id existe d√©j√†")
        
        # 4. Mettre √† jour les enregistrements existants
        print("üîÑ Mise √† jour des enregistrements existants...")
        
        # Compter les enregistrements sans client_id
        count_null = db.session.execute(text(
            "SELECT COUNT(*) FROM zone_risque_processus WHERE client_id IS NULL"
        )).scalar()
        
        print(f"üìä {count_null} enregistrements sans client_id")
        
        if count_null > 0:
            # Mettre √† jour en fonction du processus associ√©
            update_sql = """
            UPDATE zone_risque_processus 
            SET client_id = (
                SELECT p.client_id 
                FROM processus p 
                WHERE p.id = zone_risque_processus.processus_id
            )
            WHERE client_id IS NULL AND processus_id IS NOT NULL
            """
            db.session.execute(text(update_sql))
            db.session.commit()
            
            # Compter ceux restants
            remaining = db.session.execute(text(
                "SELECT COUNT(*) FROM zone_risque_processus WHERE client_id IS NULL"
            )).scalar()
            
            print(f"‚úÖ {count_null - remaining} enregistrements mis √† jour, {remaining} restants")
        
        # 5. V√©rifier les contraintes de cl√© √©trang√®re
        print("üîç V√©rification des contraintes...")
        
        try:
            # Ajouter la contrainte de cl√© √©trang√®re si elle n'existe pas
            db.session.execute(text(
                "CREATE INDEX IF NOT EXISTS idx_zone_risque_client_id ON zone_risque_processus(client_id)"
            ))
            db.session.commit()
            print("‚úÖ Index cr√©√© sur client_id")
        except Exception as e:
            print(f"‚ö†Ô∏è Erreur cr√©ation index: {e}")
        
        return jsonify({
            'success': True,
            'message': f'Table zone_risque_processus corrig√©e avec succ√®s',
            'columns': column_names,
            'updated': count_null
        })
        
    except Exception as e:
        db.session.rollback()
        print(f"‚ùå Erreur correction: {e}")
        import traceback
        traceback.print_exc()
        return jsonify({'error': str(e)}), 500

