app.jinja_env.globals['get_niveau_impact'] = get_niveau_impact
app.jinja_env.globals['get_niveau_probabilite'] = get_niveau_probabilite
app.jinja_env.globals['get_niveau_maitrise'] = get_niveau_maitrise
