@app.route('/api/kri/<int:kri_id>/evolution')
@login_required
def api_kri_evolution(kri_id):
    """API pour r√©cup√©rer les donn√©es d'√©volution d'un KRI"""
    try:
        kri = KRI.query.get_or_404(kri_id)
        
        # R√©cup√©rer les mesures tri√©es par date
        mesures = MesureKRI.query.filter_by(kri_id=kri_id)\
            .order_by(MesureKRI.date_mesure.asc())\
            .all()
        
        dates = [mesure.date_mesure.strftime('%Y-%m-%d') for mesure in mesures]
        valeurs = [float(mesure.valeur) for mesure in mesures]
        
        return jsonify({
            'success': True,
            'kri': {
                'id': kri.id,
                'nom': kri.nom,
                'unite_mesure': kri.unite_mesure
            },
            'dates': dates,
            'valeurs': valeurs
        })
        
    except Exception as e:
        return jsonify({
            'success': False,
            'error': str(e)
        }), 500

# Routes pour l'administration
@app.route('/risques')
@login_required
def liste_risques():
    """Liste des risques avec isolation multi-tenant COMPL√àTE"""
    from sqlalchemy.orm import joinedload
    from sqlalchemy import func
    
    # ========================
    # 1. FILTRE PRINCIPAL
    # ========================
    
    # CORRECTION : Utiliser get_client_filter AVEC v√©rification d'acc√®s
    base_query = get_client_filter(Risque)
    
    # Appliquer les filtres d'archivage
    base_query = base_query.filter_by(is_archived=False)
    
    # ========================
    # 2. OPTIMISATION DES JOINTURES
    # ========================
    
    # CORRECTION : Charger uniquement les derni√®res √©valuations pour les performances
    risques_query = base_query\
        .options(
            joinedload(Risque.cartographie),
            joinedload(Risque.createur),
            joinedload(Risque.kris),
            # Ne pas charger toutes les √©valuations, juste les n√©cessaires
            joinedload(Risque.evaluations)
        )\
        .order_by(Risque.created_at.desc())
    
    # ========================
    # 3. PAGINATION
    # ========================
    
    page = request.args.get('page', 1, type=int)
    per_page = request.args.get('per_page', 20, type=int)
    
    risques = risques_query.paginate(
        page=page,
        per_page=per_page,
        error_out=False
    )
    
    # ========================
    # 4. STATISTIQUES CORRIG√âES
    # ========================
    
    # CORRECTION : Utiliser une sous-requ√™te pour les statistiques critiques
    # Sous-requ√™te pour les derni√®res √©valuations
    derniere_eval_subq = db.session.query(
        EvaluationRisque.risque_id,
        func.max(EvaluationRisque.created_at).label('max_date')
    ).group_by(EvaluationRisque.risque_id).subquery()
    
    # Compter les risques critiques (derni√®re √©valuation seulement)
    risques_critiques_query = db.session.query(Risque.id).join(
        EvaluationRisque, Risque.id == EvaluationRisque.risque_id
    ).join(
        derniere_eval_subq,
        (EvaluationRisque.risque_id == derniere_eval_subq.c.risque_id) &
        (EvaluationRisque.created_at == derniere_eval_subq.c.max_date)
    ).filter(
        Risque.is_archived == False,
        EvaluationRisque.niveau_risque == 'Critique'
    )
    
    # Appliquer le filtre client √† la sous-requ√™te
    if current_user.role != 'super_admin' and hasattr(Risque, 'client_id'):
        risques_critiques_query = risques_critiques_query.filter(
            Risque.client_id == current_user.client_id
        )
    
    stats = {
        'total': get_client_filter(Risque).filter_by(is_archived=False).count(),
        'critiques': risques_critiques_query.distinct().count(),
        'en_cours': get_client_filter(Risque).filter_by(is_archived=False).count(),
        'archives': get_client_filter(Risque).filter_by(is_archived=True).count(),
        
        # Statistiques suppl√©mentaires
        'avec_evaluations': get_client_filter(Risque).filter_by(is_archived=False)
            .filter(Risque.evaluations.any()).count(),
        'sans_evaluations': get_client_filter(Risque).filter_by(is_archived=False)
            .filter(~Risque.evaluations.any()).count(),
        'avec_kri': get_client_filter(Risque).filter_by(is_archived=False)
            .filter(Risque.kris.any()).count()
    }
    
    # ========================
    # 5. V√âRIFICATION D'ACC√àS POUR CHAQUE RISQUE
    # ========================
    
    # CORRECTION : V√©rifier l'acc√®s √† chaque risque dans la liste pagin√©e
    # (optionnel mais recommand√© pour la s√©curit√©)
    if current_user.role != 'super_admin':
        for risque in risques.items:
            if not check_client_access(risque):
                print(f"‚ö†Ô∏è ALERTE: Utilisateur {current_user.username} n'a pas acc√®s au risque {risque.id}")
    
    # ========================
    # 6. R√âCUP√âRATION DES FILTRES
    # ========================
    
    # R√©cup√©rer les filtres depuis l'URL
    filtre_niveau = request.args.get('niveau')
    filtre_statut = request.args.get('statut')
    filtre_cartographie = request.args.get('cartographie_id', type=int)
    
    # ========================
    # 7. AJOUTER LA DERNI√àRE √âVALUATION √Ä CHAQUE RISQUE
    # ========================
    
    for risque in risques.items:
        # Ajouter la derni√®re √©valuation pour faciliter l'affichage
        if risque.evaluations:
            risque.derniere_evaluation = max(risque.evaluations, key=lambda x: x.created_at)
        else:
            risque.derniere_evaluation = None
        
        # Compter le nombre de KRI actifs
        risque.nb_kri_actifs = len([k for k in risque.kris if getattr(k, 'est_actif', True)])
    
    # ========================
    # 8. RENDU
    # ========================
    
    return render_template('risque/liste.html',
                         risques=risques,
                         stats=stats,
                         page=page,
                         per_page=per_page,
                         current_user=current_user,
                         filtre_niveau=filtre_niveau,
                         filtre_statut=filtre_statut,
                         filtre_cartographie=filtre_cartographie)

