@app.route('/admin/direction/nouvelle', methods=['GET', 'POST'])
@login_required
def nouvelle_direction():
    """Cr√©er une nouvelle direction"""
    # V√©rifier la permission de g√©rer les d√©partements
    if not current_user.has_permission('can_manage_departments'):
        flash('Acc√®s refus√© : permission de g√©rer les directions/services requise', 'error')
        return redirect(url_for('dashboard'))
    
    form = DirectionForm()
    
    # Filtrer les utilisateurs par client (sauf super admin)
    if current_user.role == 'super_admin':
        user_choices = User.query.all()
    else:
        user_choices = get_client_filter(User).all()
    
    form.responsable_id.choices = [(0, 'S√©lectionnez un responsable')] + [(u.id, u.username) for u in user_choices]
    
    if form.validate_on_submit():
        responsable_id = form.responsable_id.data if form.responsable_id.data and form.responsable_id.data != 0 else None
        
        direction = Direction(
            nom=form.nom.data,
            description=form.description.data,
            responsable_id=responsable_id,
            client_id=current_user.client_id if current_user.client_id else None  # Multi-tenant
        )
        db.session.add(direction)
        db.session.flush()
        
        # Notification
        if responsable_id and responsable_id != current_user.id:
            notification = Notification(
                destinataire_id=responsable_id,
                type_notification=Notification.TYPE_SYSTEME,
                titre=f"Vous √™tes responsable de la direction: {direction.nom}",
                message=f"Vous avez √©t√© d√©sign√© comme responsable de la nouvelle direction '{direction.nom}'.",
                urgence=Notification.URGENCE_IMPORTANT,
                entite_type='direction',
                entite_id=direction.id,
                donnees_supplementaires={
                    'createur': current_user.username
                }
            )
            db.session.add(notification)
        
        db.session.commit()
        flash('Direction cr√©√©e avec succ√®s', 'success')
        return redirect(url_for('admin_directions'))
    
    return render_template('admin/direction_form.html', form=form)


