@app.route('/api/logigramme/<int:activite_id>/dupliquer', methods=['POST'])
@csrf.exempt  # <-- AJOUTER CETTE LIGNE
@login_required
def api_dupliquer_logigramme(activite_id):
    activite = ProcessusActivite.query.get_or_404(activite_id)
    
    # V√©rifier que l'utilisateur est le propri√©taire
    if activite.created_by != current_user.id:
        return jsonify({'error': 'Non autoris√©'}), 403
    
    data = request.get_json()
    nom_copie = data.get('nom') if data else f"{activite.nom} (Copie)"
    
    try:
        # Cr√©er une copie du logigramme
        nouvelle_activite = ProcessusActivite(
            nom=nom_copie,
            description=activite.description,
            direction_id=activite.direction_id,
            service_id=activite.service_id,
            created_by=current_user.id
        )
        db.session.add(nouvelle_activite)
        db.session.flush()  # Pour obtenir l'ID
        
        # Copier les √©l√©ments
        for element in activite.elements:
            nouvel_element = ElementLogigramme(
                activite_id=nouvelle_activite.id,
                type_element=element.type_element,
                libelle=element.libelle,
                description=element.description,
                position_x=element.position_x,
                position_y=element.position_y,
                style=element.style
            )
            db.session.add(nouvel_element)
        
        db.session.commit()
        return jsonify({'message': 'Logigramme dupliqu√© avec succ√®s', 'id': nouvelle_activite.id})
        
    except Exception as e:
        db.session.rollback()
        return jsonify({'error': str(e)}), 500

