    # Cr√©er le CSV
    output = io.StringIO()
    writer = csv.writer(output, delimiter=';')  # Point-virgule pour Excel fran√ßais
    
    # En-t√™tes
    headers = ['Session ID', 'Statut', 'Date d√©but', 'Date fin', 'Dur√©e (s)', 'Email', 'Nom', 'IP']
